import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const [message, setMessage] = useState({text:null, type:'notify'})
  const [newTitle, setNewTitle] = useState('')
  const [newAuthor, setNewAuthor] = useState('')
  const [newUrl, setNewUrl] = useState('')
  const [loginVisible, setLoginVisible] = useState(false)

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBloglistUser')

    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({
        username, password
      })

      window.localStorage.setItem(
        'loggedBloglistUser', JSON.stringify(user)
      ) 
      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
      setMessage({text:'Successfully logged in', type:'notify'})
      setTimeout(() => {
        setMessage({text:null, type:'notify'})
      }, 5000)
    } catch (exception) {
      setMessage({text:'Wrong username or password', type:'error'})
      setTimeout(() => {
        setMessage({text:null, type:'notify'})
      }, 5000)
    }
  }

  const handleLogOut = async (event) => {
    event.preventDefault()
    try {
      window.localStorage.removeItem('loggedBloglistUser') 
      blogService.setToken('')
      setUser(null)
      setUsername('')
      setPassword('')
      setMessage({text:'Successfully logged out', type:'notify'})
      setTimeout(() => {
        setMessage({text:null, type:'notify'})
      }, 5000)
    } catch (exception) {
      setMessage({text:'Logout failed', type:'error'})
      setTimeout(() => {
        setMessage({text:null, type:'notify'})
      }, 5000)
    }
  }

  const addBlog = (event) => {
    event.preventDefault()
    const blogObject = {
      title: newTitle,
      author: newAuthor,
      url: newUrl,
    }

    blogService
      .create(blogObject)
      .then(returnedBlog => {
        setBlogs(blogs.concat(returnedBlog))
        setNewTitle('')
        setNewAuthor('')
        setNewUrl('')
        setMessage({text:`A new blog ${blogObject.title} by ${blogObject.author} added`, type:'notify'})
        setTimeout(() => {
          setMessage({text:null, type:'notify'})
        }, 5000) 
      }).catch(error => {
        setMessage({text:`Error adding new blog. Blog must have title and url.`, type:'error'})
          setTimeout(() => {
          setMessage({text:null, type:'notify'})
        }, 5000)
      })
  }

  const handleTitleChange = (event) => {
    setNewTitle(event.target.value)
  }

  const handleAuthorChange = (event) => {
    setNewAuthor(event.target.value)
  }

  const handleUrlChange = (event) => {
    setNewUrl(event.target.value)
  }

  const blogView = () => (
    <div>
      <h2>Blogs</h2>
      <div>{signOutForm()}</div>
      <p></p>
      <h3>Create new</h3>
      {blogForm()}
      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
    </div>
  )

  const signOutForm = () => (
    <form onSubmit = {handleLogOut}>
      {user.name} logged in <button type="submit">Logout</button>
    </form>
  )

  const blogForm = () => (
    <form onSubmit={addBlog}>
      <div>
        Title <input value={newTitle} onChange={handleTitleChange} />
      </div>
      <div>
        Author <input value={newAuthor} onChange={handleAuthorChange} />
      </div>
      <div>
        Url <input value={newUrl} onChange={handleUrlChange} />
      </div>
      <p></p>
      <button type="submit">save</button>
      <p></p>
    </form> 
  )

  return (
    <div>
      <Notification message = {message} />
      {user === null ? loginForm() : blogView()}
    </div>
  )
}

const loginForm = () => {
  const hideVisible = { display: loginVisible ? 'none' : '' }
  const showVisible = { display: loginVisible ? '' : 'none' }

  return (
    <div>
      <div style={hideVisible}>
        <button onClick={() => setLoginVisible(true)}>Log in</button>
      </div>
      <div style={showVisible}>
        <LoginForm username={username} password={password} 
          handleUsernameChange={({ target }) => setUsername(target.value)}
          handlePasswordChange={({ target }) => setPassword(target.value)}
          handleLogin={handleLogin}
        />
        <button onClick={() => setLoginVisible(false)}>Cancel</button>
      </div>
    </div>
  )
}

const Notification = (props) => {
  if (props.message.text === null) {
    return null
  }
  return (
    <div className = {props.message.type}>{props.message.text}</div>
  )
}

export default App
